    section div:nth-of-type(2) {
        height: 100vh;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
    }

    section div:nth-of-type(2) h3 {
        text-align: center;
        color: var(--color-3);
        font-family: 'Coder\'s Crux';
        font-size: 1.8em;
        text-transform: uppercase;
        line-height: 70%;
    }

    section div:nth-of-type(2) div {
        height: fit-content;
        margin: 3em 0 3em 0;
    }

    section div:nth-of-type(2) div button {
        margin: 0 .4em 0 .4em;
        background-color: var(--color-3);
